package fr.ekito.gwt.server.config;import org.springframework.beans.factory.config.PropertyPlaceholderConfigurer;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.ComponentScan.Filter;import org.springframework.context.annotation.Configuration;import org.springframework.core.io.ClassPathResource;import org.springframework.stereotype.Controller;import fr.ekito.gwt.server.ServerApplication;@Configuration@ComponentScan(basePackageClasses = ServerApplication.class, excludeFilters = @Filter({Controller.class, Configuration.class}))class ApplicationConfig {		@Bean	public static PropertyPlaceholderConfigurer propertyPlaceholderConfigurer() {		PropertyPlaceholderConfigurer ppc = new PropertyPlaceholderConfigurer();		ppc.setLocation(new ClassPathResource("/config/application.properties"));		return ppc;	}	}